import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function EmployeeDashboard() {
    const [transactions, setTransactions] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const authToken = localStorage.getItem('authToken');
        if (!authToken) {
            navigate('/employee-login');
        }

        const fetchTransactions = async () => {
            const response = await fetch('http://localhost:5000/api/transactions', {
                headers: {
                    'Authorization': `Bearer ${authToken}`
                },
            });
            const data = await response.json();
            setTransactions(data);
        };

        fetchTransactions();
    }, [navigate]);

    const handleVerify = async (transactionId) => {
        const authToken = localStorage.getItem('authToken');
        const response = await fetch(`http://localhost:5000/api/verify/${transactionId}`, {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${authToken}`,
            },
        });

        const data = await response.json();
        if (data.success) {
            alert('Transaction verified!');
            setTransactions(transactions.map((t) => t.id === transactionId ? { ...t, status: 'verified' } : t));
        } else {
            alert('Verification failed');
        }
    };

    return (
        <div>
            <h1>Employee Dashboard</h1>
            <ul>
                {transactions.map((transaction) => (
                    <li key={transaction.id}>
                        <p>Amount: {transaction.amount}</p>
                        <p>Currency: {transaction.currency}</p>
                        <p>Payee Account: {transaction.payeeAccount}</p>
                        <p>SWIFT Code: {transaction.swiftCode}</p>
                        <p>Status: {transaction.status}</p>
                        <button onClick={() => handleVerify(transaction.id)}>Verify</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default EmployeeDashboard;
