import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function EmployeeDashboard() {
    const [transactions, setTransactions] = useState([]);
    const [showSubmitButton, setShowSubmitButton] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const authToken = localStorage.getItem('authToken');
        if (!authToken) {
            navigate('/employee-login');
            return;
        }

        const fetchTransactions = async () => {
            const response = await fetch('http://localhost:5000/api/transactions', {
                headers: {
                    'Authorization': `Bearer ${authToken}`
                },
            });
            const data = await response.json();
            setTransactions(data);
        };

        fetchTransactions();
    }, [navigate]);

    const handleVerify = (transactionId) => {
        // Change status to 'verifying' locally
        setTransactions(transactions.map((t) =>
            t.id === transactionId ? { ...t, status: 'verifying' } : t
        ));
        setShowSubmitButton(transactionId);
    };

    const handleSubmitVerification = async (transactionId) => {
        const authToken = localStorage.getItem('authToken');
        const response = await fetch(`http://localhost:5000/api/verify/${transactionId}`, {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${authToken}`,
            },
        });

        const data = await response.json();
        if (data.success) {
            alert('Verified successfully!');
            setTransactions(transactions.map((t) =>
                t.id === transactionId ? { ...t, status: 'verified' } : t
            ));
            setShowSubmitButton(null);
        } else {
            alert('Verification failed');
            // Optionally revert the status to its previous value if verification fails
            setTransactions(transactions.map((t) =>
                t.id === transactionId ? { ...t, status: 'pending' } : t
            ));
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div>
                <h1 style={{ textAlign: 'center' }}>Employee Dashboard</h1>
                <table style={{ margin: 'auto', borderCollapse: 'collapse', width: '80%' }}>
                    <thead>
                        <tr>
                            <th>Amount</th>
                            <th>Currency</th>
                            <th>Payee Account</th>
                            <th>SWIFT Code</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {transactions.map((transaction) => (
                            <tr key={transaction.id}>
                                <td>{transaction.amount}</td>
                                <td>{transaction.currency}</td>
                                <td>{transaction.payeeAccount}</td>
                                <td>{transaction.swiftCode}</td>
                                <td>{transaction.status}</td>
                                <td>
                                    <button
                                        onClick={() => handleVerify(transaction.id)}
                                        style={{ backgroundColor: 'green', color: 'white', marginRight: '8px' }}
                                    >
                                        Verify
                                    </button>
                                    {showSubmitButton === transaction.id && (
                                        <button
                                            onClick={() => handleSubmitVerification(transaction.id)}
                                            style={{ marginLeft: '8px' }}
                                        >
                                            Submit
                                        </button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default EmployeeDashboard;
