import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Register from './components/Register';
import Login from './components/Login';
import Payment from './components/Payment';

function App() {
  const styles = {
    container: {
      maxWidth: '100%',
      height: '100vh', // Fills the entire screen height
      margin: '0 auto',
      padding: '30px',
      textAlign: 'center',
      backgroundColor: '#C4DAD2', // Updated background color
      color: '#508D4E', // Updated text color
    },
    heading: {
      color: '#508D4E', // Updated heading color
      marginBottom: '20px',
      fontSize: '32px', // Increased font size for better visibility
    },
    paragraph: {
      fontSize: '20px',
      color: '#444',
      marginBottom: '30px',
    },
    link: {
      color: '#508D4E', // Updated link color
      textDecoration: 'none',
      margin: '0 10px',
      fontWeight: 'bold',
    },
    linkHover: {
      textDecoration: 'underline',
    },
  };

  return (
    <Router>
      <div style={styles.container}>
        <h1 style={styles.heading}>Welcome to the Customer Portal</h1>
        <p style={styles.paragraph}>
          Please navigate to 
          <Link to="/register" style={styles.link}> Register</Link> 
          or 
          <Link to="/login" style={styles.link}> Login</Link>.
        </p>
        <Routes>
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
          <Route path="/payment" element={<Payment />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
