import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function EmployeeDashboard() {
    const [transactions, setTransactions] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const authToken = localStorage.getItem('authToken');
        if (!authToken) {
            navigate('/employee-login');
            return;
        }

        const fetchTransactions = async () => {
            const response = await fetch('http://localhost:5000/api/transactions', {
                headers: {
                    'Authorization': `Bearer ${authToken}`
                },
            });
            const data = await response.json();
            setTransactions(data);
        };

        fetchTransactions();
    }, [navigate]);

    const handleVerify = async (transactionId) => {
        const authToken = localStorage.getItem('authToken');
        const response = await fetch(`http://localhost:5000/api/verify/${transactionId}`, {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${authToken}`,
            },
        });

        const data = await response.json();
        if (data.success) {
            alert('Verified successfully!');
            setTransactions(transactions.map((t) =>
                t.id === transactionId ? { ...t, status: 'verified' } : t
            ));
        } else {
            alert('Verification failed');
        }
    };

    const handleCancel = (transactionId) => {
        setTransactions(transactions.filter((transaction) => transaction.id !== transactionId));
    };

    return (
        <div>
            <h1>Employee Dashboard</h1>
            <table>
                <thead>
                    <tr>
                        <th>Amount</th>
                        <th>Currency</th>
                        <th>Payee Account</th>
                        <th>SWIFT Code</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map((transaction) => (
                        <tr key={transaction.id}>
                            <td>{transaction.amount}</td>
                            <td>{transaction.currency}</td>
                            <td>{transaction.payeeAccount}</td>
                            <td>{transaction.swiftCode}</td>
                            <td>{transaction.status}</td>
                            <td>
                                <button onClick={() => handleVerify(transaction.id)}>Verify</button>
                                <button onClick={() => handleCancel(transaction.id)}>Cancel</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default EmployeeDashboard;
