import React, { useState, useEffect } from 'react';
import axios from 'axios';

function EmployeeDashboard() {
    const [transactions, setTransactions] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [activeTransactionId, setActiveTransactionId] = useState(null);

    // Effect to fetch transactions initially
    useEffect(() => {
        fetchTransactions();
    }, []);

    // Function to fetch transactions
    const fetchTransactions = async () => {
        try {
            const response = await axios.get('/api/transactions');
            setTransactions(response.data);
        } catch (error) {
            console.error('Error fetching transactions:', error);
        }
    };

    // Function to handle verification of a transaction
    const handleVerify = async (transactionId) => {
        setIsLoading(true);
        setActiveTransactionId(transactionId);
        try {
            const { data } = await axios.post(`/api/transactions/${transactionId}/verify`);
            setTransactions(transactions.map(tx =>
                tx._id === transactionId ? { ...tx, status: data.transaction.status } : tx
            ));
        } catch (error) {
            console.error('Failed to verify transaction:', error);
        }
        setIsLoading(false);
        setActiveTransactionId(null);
    };

    return (
        <div>
            <h1>Employee Dashboard</h1>
            <table>
                <thead>
                    <tr>
                        <th>Amount</th>
                        <th>Currency</th>
                        <th>Payee Account</th>
                        <th>SWIFT Code</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map((transaction) => (
                        <tr key={transaction._id}>
                            <td>{transaction.amount}</td>
                            <td>{transaction.currency}</td>
                            <td>{transaction.payeeAccount}</td>
                            <td>{transaction.swiftCode}</td>
                            <td>{transaction.status}</td>
                            <td>
                                <button
                                    onClick={() => handleVerify(transaction._id)}
                                    disabled={isLoading || (activeTransactionId && activeTransactionId !== transaction._id)}
                                >
                                    Verify
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default EmployeeDashboard;
