import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';  // Using axios for API requests

function EmployeeDashboard() {
    const [transactions, setTransactions] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [activeTransactionId, setActiveTransactionId] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const authToken = localStorage.getItem('authToken');
        if (!authToken) {
            navigate('/employee-login');
            return;
        }

        const fetchTransactions = async () => {
            try {
                const response = await axios.get('/api/transactions', {
                    headers: {
                        'Authorization': `Bearer ${authToken}`,
                    },
                });
                setTransactions(response.data.map(transaction => ({
                    ...transaction,
                    isVerifying: false,
                    submitted: false
                })));
            } catch (error) {
                console.error('Error fetching transactions:', error);
            }
        };

        fetchTransactions();
    }, [navigate]);

    const handleVerify = async (transactionId) => {
        setIsLoading(true);
        setActiveTransactionId(transactionId);
        try {
            const response = await axios.post(`/api/transactions/${transactionId}/verify`, {}, {
                headers: {
                    'Authorization': `Bearer ${authToken}`,
                },
            });
            setTransactions(transactions.map(tx =>
                tx.id === transactionId ? { ...tx, status: response.data.transaction.status } : tx
            ));
        } catch (error) {
            console.error('Failed to verify transaction:', error);
        }
        setIsLoading(false);
        setActiveTransactionId(null);
    };

    const handleSubmitVerification = (transactionId) => {
        setTransactions(transactions.map(transaction =>
            transaction.id === transactionId ? { ...transaction, isVerifying: false, submitted: true } : transaction
        ));
        setTimeout(() => {
            setTransactions(transactions.map(transaction =>
                transaction.id === transactionId ? { ...transaction, submitted: false } : transaction
            ));
        }, 3000);
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'flex-start', alignItems: 'center', height: '100vh', marginTop: '20px' }}>
            <div style={{ width: '90%', maxWidth: '1000px' }}>
                <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>Employee Dashboard</h1>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                    <thead>
                        <tr style={{ backgroundColor: '#f0f0f0', textAlign: 'left' }}>
                            <th style={{ padding: '8px' }}>Amount</th>
                            <th style={{ padding: '8px' }}>Currency</th>
                            <th style={{ padding: '8px' }}>Payee Account</th>
                            <th style={{ padding: '8px' }}>SWIFT Code</th>
                            <th style={{ padding: '8px' }}>Status</th>
                            <th style={{ padding: '8px' }}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {transactions.map((transaction) => (
                            <tr key={transaction.id}>
                                <td style={{ padding: '8px' }}>{transaction.amount}</td>
                                <td style={{ padding: '8px' }}>{transaction.currency}</td>
                                <td style={{ padding: '8px' }}>{transaction.payeeAccount}</td>
                                <td style={{ padding: '8px' }}>{transaction.swiftCode}</td>
                                <td style={{ padding: '8px' }}>{transaction.submitted ? 'submitted' : (transaction.isVerifying ? 'verifying' : transaction.status)}</td>
                                <td style={{ padding: '8px' }}>
                                    {!transaction.isVerifying && !transaction.submitted && (
                                        <button
                                            onClick={() => handleVerify(transaction.id)}
                                            disabled={isLoading || (activeTransactionId && activeTransactionId !== transaction.id)}
                                            style={{ backgroundColor: 'green', color: 'white', marginRight: '8px' }}
                                        >
                                            Verify
                                        </button>
                                    )}
                                    {transaction.isVerifying && (
                                        <button
                                            onClick={() => handleSubmitVerification(transaction.id)}
                                            style={{ backgroundColor: 'blue', color: 'white' }}
                                        >
                                            Submit
                                        </button>
                                    )}
                                    {transaction.submitted && (
                                        <span style={{ color: 'green', fontWeight: 'bold' }}>
                                            Submitted to SWIFT
                                        </span>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default EmployeeDashboard;
