import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function EmployeeDashboard() {
    const [transactions, setTransactions] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const authToken = localStorage.getItem('authToken');
        if (!authToken) {
            navigate('/employee-login');
            return;
        }

        const fetchTransactions = async () => {
            const response = await fetch('http://localhost:5000/api/transactions', {
                headers: {
                    'Authorization': `Bearer ${authToken}`,
                },
            });
            const data = await response.json();
            setTransactions(data.map(transaction => ({
                ...transaction,
                isVerifying: false,
                submitted: false
            })));
        };

        fetchTransactions();
    }, [navigate]);

    const handleVerify = (transactionId) => {
        setTransactions(transactions.map(transaction =>
            transaction.id === transactionId ? { ...transaction, isVerifying: true } : transaction
        ));
    };

    const handleSubmitVerification = (transactionId) => {
        setTransactions(transactions.map(transaction =>
            transaction.id === transactionId ? { ...transaction, submitted: true, isVerifying: false } : transaction
        ));

        setTimeout(() => {
            setTransactions(transactions.map(transaction =>
                transaction.id === transactionId ? { ...transaction, submitted: false } : transaction
            ));
        }, 3000); // Optionally hide the submitted message after a delay
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div>
                <h1 style={{ textAlign: 'center' }}>Employee Dashboard</h1>
                <table style={{ margin: 'auto', borderCollapse: 'collapse', width: '80%' }}>
                    <thead>
                        <tr>
                            <th>Amount</th>
                            <th>Currency</th>
                            <th>Payee Account</th>
                            <th>SWIFT Code</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {transactions.map((transaction) => (
                            <tr key={transaction.id}>
                                <td>{transaction.amount}</td>
                                <td>{transaction.currency}</td>
                                <td>{transaction.payeeAccount}</td>
                                <td>{transaction.swiftCode}</td>
                                <td>{transaction.isVerifying ? 'verifying' : (transaction.submitted ? 'submitted' : transaction.status)}</td>
                                <td>
                                    {!transaction.isVerifying && !transaction.submitted && (
                                        <button
                                            onClick={() => handleVerify(transaction.id)}
                                            style={{ backgroundColor: 'green', color: 'white', marginRight: '8px' }}
                                        >
                                            Verify
                                        </button>
                                    )}
                                    {transaction.isVerifying && (
                                        <button
                                            onClick={() => handleSubmitVerification(transaction.id)}
                                            style={{ marginLeft: '8px' }}
                                        >
                                            Submit
                                        </button>
                                    )}
                                    {transaction.submitted && (
                                        <span
                                            style={{
                                                color: 'green',
                                                marginLeft: '10px',
                                                fontWeight: 'bold',
                                            }}
                                        >
                                            Submitted to SWIFT
                                        </span>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default EmployeeDashboard;
